mapper.insert:
	    
		insert into sop_interview_record (id, project_id, file_id,view_date, 
		  view_target, view_notes,view_notes_text, created_time,created_id,update_time
      	)
	    values (#{id,jdbcType=BIGINT}, #{projectId,jdbcType=BIGINT}, #{fileId,jdbcType=BIGINT}, #{viewDate,jdbcType=TIMESTAMP}, 
	      #{viewTarget,jdbcType=VARCHAR}, #{viewNotes,jdbcType=VARCHAR}, #{viewNotesText,jdbcType=VARCHAR}, #{createdTime,jdbcType=BIGINT},#{createdId,jdbcType=BIGINT}
		,#{createdTime,jdbcType=BIGINT})

mapper.update:
	update sop_interview_record
	
	   project_id = #{projectId,jdbcType=BIGINT}, 
	   file_id = #{fileId,jdbcType=BIGINT}, 
       view_date = #{viewDate,jdbcType=TIMESTAMP}, 
       view_target = #{viewTarget,jdbcType=VARCHAR}, 
       view_notes = #{viewNotes,jdbcType=VARCHAR}, 
       view_notes_text = #{viewNotesText,jdbcType=VARCHAR}, 
       created_time = #{createdTime,jdbcType=BIGINT}, 
        created_id = #{createdId,jdbcType=BIGINT}, 
        update_time = #{updatedTime,jdbcType=BIGINT} 
   
       
	
	
		
			 and id =#{id}
			 and project_id = #{projectId,jdbcType=BIGINT} 
      		and file_id = #{fileId,jdbcType=BIGINT} 
      		and view_date = #{viewDate,jdbcType=TIMESTAMP} 
      		 and view_target = #{viewTarget,jdbcType=VARCHAR} 
      		 and view_notes = #{viewNotes,jdbcType=VARCHAR} 
      		 and view_notes_text = #{viewNotesText,jdbcType=VARCHAR} 
      		 and created_time = #{createdTime,jdbcType=BIGINT} 
      	     and created_id = #{createdId,jdbcType=BIGINT}

update sop_interview_record
project_id = #{projectId,jdbcType=BIGINT}, file_id = #{fileId,jdbcType=BIGINT}, view_date = #{viewDate,jdbcType=TIMESTAMP}, view_target = #{viewTarget,jdbcType=VARCHAR}, view_notes = #{viewNotes,jdbcType=VARCHAR}, view_notes_text = #{viewNotesText,jdbcType=VARCHAR}, created_time = #{createdTime,jdbcType=BIGINT}, created_id= #{createdId,jdbcType=BIGINT}, update_time = #{updatedTime,jdbcType=BIGINT}
where id = #{id}

mapper.select:
select ir.id, ir.project_id, p.project_name, ir.file_id, ir.view_date, ir.view_target, ir.view_notes,ir.view_notes_text, ir.created_time from sop_interview_record ir , sop_project p where ir.project_id = p.id and p.create_uid = #{uid,jdbcType=BIGINT} and p.id = #{projectId,jdbcType=BIGINT} and ir.view_date >= #{startTime,jdbcType=TIMESTAMP} and ir.view_date <= #{endTime,jdbcType=TIMESTAMP}
and ( p.project_name like CONCAT("%",#{keyword},"%") escape '/' or p.project_code like CONCAT("%",#{keyword},"%") escape '/' )
order by ir.view_date desc asc ,ir.created_time desc asc
order by ir.view_date desc,ir.created_time desc
limit #{offset}, #{limit}

  select count(*) from (
	select
		ir.id, ir.project_id, p.project_name, ir.file_id, ir.view_date, ir.view_target, 
		ir.view_notes, ir.view_notes_text, ir.created_time
	from sop_interview_record ir , sop_project p 
	where ir.project_id = p.id
		 and p.create_uid = #{uid,jdbcType=BIGINT} 
		 and p.id = #{projectId,jdbcType=BIGINT} 
		   and ir.view_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and ir.view_date <= #{endTime,jdbcType=TIMESTAMP}  
		
			and (
				p.project_name like CONCAT("%",#{keyword},"%")    escape '/'  
				or p.project_code like CONCAT("%",#{keyword},"%")   escape '/'  
			)
		
	) a
