mapper.select:
select
	sm.id, sm.project_id, p.project_name, sm.file_id, sm.meeting_date, sm.meeting_type, 
	sm.meeting_result, sm.meeting_notes,sm.meeting_notes_text, sm.created_time
from sop_meeting_record sm , sop_project p 
where sm.project_id = p.id
	 and p.create_uid = #{uid,jdbcType=BIGINT} 
	 and p.id = #{projectId,jdbcType=BIGINT} 
	 and sm.meeting_type = #{meetingType} 
	   and sm.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
	  and sm.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
	
		and (
			p.project_name like CONCAT("%",#{keyword},"%")   escape '/'  
		 or p.project_code like CONCAT("%",#{keyword},"%")   escape '/'  
		)
	
	 and sm.record_type = #{recordType,jdbcType=TINYINT} 	
	
	
order by sm.meeting_date desc,sm.created_time desc

	limit #{offset}, #{limit}

  select count(*) from (
  	select
		sm.id, sm.project_id, p.project_name, sm.file_id, sm.meeting_date, sm.meeting_type, 
		sm.meeting_result, sm.meeting_notes, sm.meeting_notes_text,sm.created_time
	from sop_meeting_record sm , sop_project p 
	where sm.project_id = p.id
		 and p.create_uid = #{uid,jdbcType=BIGINT} 
		 and p.id = #{projectId,jdbcType=BIGINT} 
		 and sm.meeting_type = #{meetingType} 
		   and sm.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and sm.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
		
			and (
				p.project_name like CONCAT("%",#{keyword},"%")    escape '/'  
				or p.project_code like CONCAT("%",#{keyword},"%")   escape '/'  
			)
		
		 and sm.record_type = #{recordType,jdbcType=TINYINT} 	
	) a

select max(meeting_name) from sop_meeting_record
where meeting_type = #{meetingType}
and	  project_id = #{projectId}

	select count(*) 
	from sop_meeting_record meet,sop_project pro
	where
		meet.project_id = pro.id
		
         and meet.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
	     and meet.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
	   
	   and meet.meeting_type = #{meetingType,jdbcType=VARCHAR} 
	  
      and meet.meeting_result = #{meetingResult,jdbcType=VARCHAR} 
	  
	   and meet.record_type = #{recordType,jdbcType=TINYINT} 	
	  
	   and pro.project_type = #{projectType,jdbcType=VARCHAR} 
	   and pro.project_departId = #{departId,jdbcType=BIGINT}

	select
		pro.create_uid, pro.project_departId departId, count(meet.id) passMeetNum
	from sop_meeting_record meet,sop_project pro
	where
		meet.project_id = pro.id
		 and pro.project_type = #{projectType,jdbcType=VARCHAR} 
		 and pro.project_departId = #{departId,jdbcType=BIGINT} 
		
			AND pro.create_uid IN
			
				#{item}
			
		
		
		   and meet.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and meet.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
	
		and meet.meeting_type = #{meetingType,jdbcType=VARCHAR} 
	
		and meet.meeting_result = #{meetingResult,jdbcType=VARCHAR} 
	
		 and meet.record_type = #{recordType,jdbcType=TINYINT} 

	GROUP BY pro.create_uid

	select
		pro.project_departId departId, count(meet.id) passMeetNum
	from sop_meeting_record meet,sop_project pro
	where
		meet.project_id = pro.id
		 and pro.project_type = #{projectType,jdbcType=VARCHAR} 
		
			AND pro.create_uid IN
			
				#{item}
			
		
		
		
			AND pro.project_departId IN
			
				#{item}
			
		
		
		   and meet.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and meet.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
	
		and meet.meeting_type = #{meetingType,jdbcType=VARCHAR} 
	
		and meet.meeting_result = #{meetingResult,jdbcType=VARCHAR} 
	
		 and meet.record_type = #{recordType,jdbcType=TINYINT} 

	GROUP BY pro.project_departId

select
	pro.id project_id,pro.project_departId departId
from sop_meeting_record meet,sop_project pro
where
	meet.project_id = pro.id
	
	   and meet.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
	  and meet.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  

	and meet.meeting_type = #{meetingType,jdbcType=VARCHAR} 

	and meet.meeting_result = #{meetingResult,jdbcType=VARCHAR} 

	 and meet.record_type = #{recordType,jdbcType=TINYINT} 
	
	 and pro.project_type = #{projectType,jdbcType=VARCHAR} 
	
		AND pro.create_uid IN
		
			#{item}
		
	
	
	
		AND pro.project_departId IN
		
			#{item}
		
	
GROUP BY pro.id

SELECT  a.departId,a.create_uid, COUNT(a.project_id) sumProNum
FROM
	(
	SELECT
		pro.id project_id, pro.create_uid,pro.project_departId departId
	FROM 
		sop_meeting_record meet,sop_project pro
	WHERE
		meet.project_id = pro.id
		   and meet.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and meet.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
	
		and meet.meeting_type = #{meetingType,jdbcType=VARCHAR} 
	
		and meet.meeting_result = #{meetingResult,jdbcType=VARCHAR} 
	
		 and meet.record_type = #{recordType,jdbcType=TINYINT} 
		
		 and pro.project_type = #{projectType,jdbcType=VARCHAR} 
		
			AND pro.create_uid IN
			
				#{item}
			
		
		
		
			AND pro.project_departId IN
			
				#{item}
			
		
	GROUP BY pro.id	
	) a
GROUP BY a.departId

SELECT  a.departId,a.create_uid, COUNT(a.project_id) sumProNum
FROM
	(
	SELECT
		pro.id project_id, pro.create_uid,pro.project_departId departId
	FROM 
		sop_meeting_record meet,sop_project pro
	WHERE
		meet.project_id = pro.id
		   and meet.meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and meet.meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
	
		and meet.meeting_type = #{meetingType,jdbcType=VARCHAR} 
	
		and meet.meeting_result = #{meetingResult,jdbcType=VARCHAR} 
	
		 and meet.record_type = #{recordType,jdbcType=TINYINT} 
		
		 and pro.project_type = #{projectType,jdbcType=VARCHAR} 
		
			AND pro.create_uid IN
			
				#{item}
			
		
		
		 and pro.project_departId = #{departId,jdbcType=BIGINT} 
		
			AND pro.project_departId IN
			
				#{item}
			
		
	GROUP BY pro.id	
	) a
GROUP BY a.create_uid

SELECT 
	a.*,b.pass_meet_time,b.last_created_time
FROM 
	(
	SELECT 
		project_id,meeting_type,MIN(meeting_date) first_meet_time,MIN(created_time) first_created_time
	FROM 
		sop_meeting_record 
	WHERE 
	  
	    
			and project_id in
			
				#{item}
			
	    
		   and meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
	
		and meeting_type = #{meetingType,jdbcType=VARCHAR} 
		 and record_type = #{recordType,jdbcType=TINYINT} 
	  
	GROUP BY project_id,meeting_type
	) a
	LEFT JOIN
	(
	SELECT 
		project_id,meeting_type,MAX(meeting_date) pass_meet_time,MAX(created_time) last_created_time
	FROM 
		sop_meeting_record 
	WHERE 
	  
	 	 
			and project_id in
			
				#{item}
			
	    
		   and meeting_date >= #{startTime,jdbcType=TIMESTAMP}  
		  and meeting_date <= #{endTime,jdbcType=TIMESTAMP}  
		and meeting_type = #{meetingType,jdbcType=VARCHAR} 
		and meeting_result = #{meetingResult,jdbcType=VARCHAR} 
		 and record_type = #{recordType,jdbcType=TINYINT} 
	  
	GROUP BY project_id,meeting_type
	)b
	ON a.project_id = b.project_id AND a.meeting_type = b.meeting_type
