mapper.update:
update information_title
parent_id = #{parentId,jdbcType=VARCHAR}, code = #{code,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, index_no = #{indexNo,jdbcType=DOUBLE}, type = #{type,jdbcType=INTEGER}, sign = #{sign,jdbcType=INTEGER}, content = #{content,jdbcType=VARCHAR}, is_show = #{isShow,jdbcType=CHAR}, is_valid = #{isValid,jdbcType=INTEGER}, placeholder = #{placeholder,jdbcType=VARCHAR}, created_time = #{createdTime,jdbcType=BIGINT}, create_id = #{createId,jdbcType=BIGINT}, updated_time = #{updatedTime,jdbcType=BIGINT}, update_id = #{updateId,jdbcType=BIGINT},
where id = #{id}

update information_title
parent_id = #{parentId,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, index_no = #{indexNo,jdbcType=DOUBLE}, type = #{type,jdbcType=INTEGER}, sign = #{sign,jdbcType=INTEGER}, content = #{content,jdbcType=VARCHAR}, is_show = #{isShow,jdbcType=CHAR}, is_valid = #{isValid,jdbcType=INTEGER}, placeholder = #{placeholder,jdbcType=VARCHAR},
and id = #{id,jdbcType=BIGINT} and parent_id = #{parentId,jdbcType=VARCHAR} and code = #{code,jdbcType=VARCHAR}

mapper.select:
select
id, parent_id, code, name,  type, sign, content,is_show, placeholder,is_must,val_rule,val_rule_mark
from information_title 


		 and id = #{id,jdbcType=BIGINT} 
		 and  parent_id = #{parentId,jdbcType=VARCHAR} 
		 and  code = #{code,jdbcType=VARCHAR} 
		 and  name = #{name,jdbcType=VARCHAR} 
		 and  index_no = #{indexNo,jdbcType=INTEGER} 
		 and  type = #{type,jdbcType=INTEGER} 
		 and  sign = #{sign,jdbcType=INTEGER} 
		 and  is_valid = #{isValid,jdbcType=INTEGER} 
		 and  create_id = #{createId,jdbcType=BIGINT} 
		 and  update_id = #{updateId,jdbcType=BIGINT} 


	order by ${sorting}

SELECT
t.id, r.parent_id, r.code, CASE WHEN r.name IS NOT NULL THEN r.name ELSE t.name END AS name,  
t.type, t.sign, t.content,
t.is_show, t.placeholder,t.is_must,
t.val_rule,t.val_rule_mark,r.id AS relate_id 
FROM information_title t
INNER JOIN information_title_relate r ON r.title_id = t.id


		 and r.id = #{id,jdbcType=BIGINT} 
		 and  r.parent_id = #{parentId,jdbcType=VARCHAR} 
		 and  r.code like CONCAT( #{code,jdbcType=VARCHAR},'%') 
		 and  r.is_valid = #{isValid,jdbcType=INTEGER} 


order by ${sorting}
